key: {
    type: DataTypes.STRING,
    validate : {
        is: /^[a-z]+$/i,          // 匹配这个 RegExp
        isInt: {
            msg: "必须是价格的整数"
        },
        isIn: {
            args: [['en', 'zh']],
            msg: "必须为英文或中文"
        },
        isEven(value) {  //自定义规则
            if (parseInt(value) % 2 !== 0) {
                throw new Error('Only even values are allowed!');
            }
        }
    }
}
